mapper.select:
SELECT * FROM BC_PRODREV WHERE 1=1
AND P_NAME LIKE CONCAT('%', #{searchKeyword}, '%') AND P_NO LIKE CONCAT('%', #{searchKeyword}, '%') AND PR_STAR LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY PR_NO DESC LIMIT #{sizePerPage} OFFSET #{startList}

mapper.insert:
INSERT INTO BC_PRODREV (U_NO, U_NICK, P_NO, P_NAME, PR_CONTENT, PR_STAR, PR_IMG)
SELECT u.U_NO, u.U_NICK, p.P_NO, p.P_NAME, #{pr_content}, #{pr_star}, #{pr_img}
FROM bc_users AS u
JOIN bc_prod AS p ON p.P_NO = #{p_no}
WHERE u.U_NO = #{u_no}

mapper.select:
SELECT pr.*, o.O_NO 
FROM BC_PRODREV AS pr 
JOIN BC_ORDER AS o on pr.U_NO = #{u_no} 
WHERE o.U_NO = #{u_no} and o.P_NO = #{p_no} and PR_NO = #{pr_no}
